function register(address newTokenHolder, uint issueAmount) public { // Mint tokens and assign to new owner
    require(active, "Contract is not active");
    require(msg.sender == owner, "Only owner can register");
    require(balances[newTokenHolder] == 0, "Address already registered");
    require(issueAmount > 0, "Issue amount must be positive");

    require(_totalSupply + issueAmount > _totalSupply, "Overflow risk");
    _totalSupply += issueAmount;
    emit Mint(newTokenHolder, issueAmount);  // Trigger event

