function postWish(bytes memory name, bytes memory content) public payable {
    require(msg.value > 0, "Message value must be greater than 0");
    require(serviceProvider != address(0), "Invalid service provider");
    uint256 serviceFee = msg.value * serviceFeeNumerator / (serviceFeeDenominator + serviceFeeNumerator);
    uint256 tribute = msg.value - serviceFee;
