    string public constant name = "eElectroneum";
    string public constant symbol = "eETN";
    uint8 public constant decimals = 8;
    uint256 public constant SUPPLY_CAP = 21000000 * (10 ** uint256(decimals));
    address NULL_ADDRESS = address(0);
    uint public nonce = 0;

    event NonceTick(uint nonce);
    function incNonce() public {
        nonce += 1;
        if(nonce > 100) {
            nonce = 0;
        }
        emit NonceTick(nonce);
    }

    event NoteChanged(string newNote);
    string public note = "The future of cryptocurrency is us!";
    function setNote(string memory note_) public onlyOwner {
        require(bytes(note_).length > 0, "Note cannot be empty");
        note = note_;
        emit NoteChanged(note);
