    function drop(address[] memory addresses, uint256 amount) public onlyOwner {
        uint256 amt = amount * 10**8;
        require(amt > 0, "Amount must be greater than 0");
        require(amt <= SUPPLY_CAP, "Amount exceeds supply cap");
